package org.matt.apps;import java.io.IOException;import java.io.PrintStream;import java.util.Enumeration;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;@WebServlet({ "/GatherList" })public class RecipeServlet extends HttpServlet {	private static final long serialVersionUID = 1L;	protected void doPost(HttpServletRequest request, HttpServletResponse response)			throws ServletException, IOException {		String[] itemListSorted = null;		int i = 0;		Enumeration<String> itemList = request.getParameterNames();		while (itemList.hasMoreElements()) {			String item = (String) itemList.nextElement();			String[] paramValues = request.getParameterValues(item);			for (int n = 0; n < paramValues.length; n++) {				String paramValue = paramValues[n];				System.out.println("item " + i + " is: " + paramValue);			}			i++;		}	}}